package com.project.impl;import com.project.api.ProblemStatements;import org.springframework.stereotype.Component;import javax.ws.rs.core.Response;import javax.ws.rs.core.Response.Status;@Componentpublic class ProblemStatementsImpl implements ProblemStatements {    @Override    public Response getSumAndAverage(String[] arr) {        System.out.println("Log request: ");        int i = 0;        int sum = 0, average = 0;        StringBuffer res = new StringBuffer("");        try {            for (i = 0; i < arr.length; i++) {                sum += Integer.parseInt(arr[i]);            }            if (!(sum == 0)) {                res.append("Sum: " + sum);                average = sum / i;            }            if (!(average == 0)) {                res.append("\naverage: " + average);                StringBuffer greaterThanAverage = new StringBuffer("greaterThanAverage: [");                boolean flag = false;                for (int j = 0; j < arr.length; j++) {                    if (average > Integer.parseInt(arr[j])) {                        flag = true;                        greaterThanAverage.append(arr[j] + " ");                    }                }                if (flag) {                    res.append(greaterThanAverage);                }            }        } catch (Exception e) {            res.append("â€œNumeric input expected, received invalid numbers in input");            return Response.status(Status.BAD_REQUEST).entity(res).build();        }        return Response.status(Status.OK).entity(res).build();    }}