package com.project.project;import com.project.impl.ProblemStatementsImpl;import org.junit.Assert;import org.junit.Test;import javax.ws.rs.core.Response;public class ProblemStatementsImplTest {    @Test    public void testSum(){        ProblemStatementsImpl problemStatements = new ProblemStatementsImpl();        String[] arr = new String[]{"10","20","30"};        Response sumAndAverage = problemStatements.getSumAndAverage(arr);        Assert.assertEquals(200, sumAndAverage.getStatus());        String[] arr2 = new String[]{"10A","20B","30C"};        sumAndAverage = problemStatements.getSumAndAverage(arr2);        Assert.assertEquals(400, sumAndAverage.getStatus());    }}